function addClickListener() {
    this.document.addEvent('click', function(e) {

        if (!e.target) 
            targetEle = e.srcElement;
        else 
            targetEle = e.target;

        if (targetEle.nodeName == "A" && targetEle.href && targetEle.href != "") {
            if (isInternal(targetEle.href, tempArray)) {
                //internal link
                return true;
            } else {
                //external link : save url in cookie, open popup survey
                createSurveyCookie('europa_esurvey_url', targetEle.href, 0);
                TB_show('', urlRand(survey_url), '', '0');
                return false;
            }
        } else {
            //alert('not a');
            parentElt = targetEle.parentNode;

            if (parentElt.nodeName == "A" && parentElt.href && parentElt.href != "") {
                if (isInternal(parentElt.href, tempArray)) {
                    //internal link
                    return true;
                } else {
                    //external link : save url in cookie, open popup survey
                    createSurveyCookie('europa_esurvey_url', parentElt.href, 0);
                    TB_show('', urlRand(survey_url), '', '0');
                    return false;
                }
            } else {
                return true;
            }
        }


    });
}
function isInternal(tUrl, urlList) {
    var isIn = false;
    var tStart;
    //clean url
    if (tUrl.indexOf('http://')!=-1) 
        tStart = 7;
    else 
        tStart = 0;
    tUrl = tUrl.substring(tStart, tUrl.lastIndexOf('/'));

    for (var i = 0; i < urlList.length; i++) {
        if (urlList[i].indexOf(tUrl) != - 1) 
            isIn = true;
    }
    return isIn;
}

